pragma solidity ^0.8.0;

contract Employee {
    // Structure to define an employee
    struct EmployeeInfo {
        uint256 id;
        string name;
        string designation;
        uint256 department; // Can be a string or an enum for departments
    }

    // Mapping to store employees (key: employee ID, value: EmployeeInfo struct)
    mapping(uint256 => EmployeeInfo) public employees;

    // Current employee ID (auto-increments)
    uint256 public nextEmployeeId = 1;

    // Event to be emitted when an employee is added
    event EmployeeAdded(uint256 id, string name, string designation, uint256 department);

    // Event to be emitted when an employee is updated
    event EmployeeUpdated(uint256 id, string name, string designation, uint256 department);

    // Event to be emitted when an employee is deleted
    event EmployeeDeleted(uint256 id);

    // Function to add a new employee
    function addEmployee(string memory _name, string memory _designation, uint256 _department) public {
        employees[nextEmployeeId] = EmployeeInfo(nextEmployeeId, _name, _designation, _department);
        nextEmployeeId++;
        emit EmployeeAdded(nextEmployeeId - 1, _name, _designation, _department);
    }

    // Function to update an existing employee's details
    function updateEmployee(uint256 _id, string memory _name, string memory _designation, uint256 _department) public {
        require(employees[_id].id > 0, "Employee does not exist");
        employees[_id].name = _name;
        employees[_id].designation = _designation;
        employees[_id].department = _department;
        emit EmployeeUpdated(_id, _name, _designation, _department);
    }

    // Function to delete an employee
    function deleteEmployee(uint256 _id) public {
        require(employees[_id].id > 0, "Employee does not exist");
        delete employees[_id];
        emit EmployeeDeleted(_id);
    }
}
