// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EmployeeManagement {
    // Structure to store employee details
    struct Employee {
        uint id;
        string name;
        uint salary;
        bool isActive;
    }
    
    // Mapping to store employees
    mapping(address => Employee) public employees;
    
    // Event to log new employee addition
    event EmployeeAdded(address indexed employeeAddress, uint id, string name, uint salary);
    
    // Function to add a new employee
    function addEmployee(address _employeeAddress, uint _id, string memory _name, uint _salary) public {
        require(employees[_employeeAddress].id == 0, "Employee already exists");
        
        employees[_employeeAddress] = Employee(_id, _name, _salary, true);
        emit EmployeeAdded(_employeeAddress, _id, _name, _salary);
    }
    
    // Function to update employee details
    function updateEmployee(address _employeeAddress, uint _id, string memory _name, uint _salary, bool _isActive) public {
        require(employees[_employeeAddress].id != 0, "Employee does not exist");
        
        employees[_employeeAddress].id = _id;
        employees[_employeeAddress].name = _name;
        employees[_employeeAddress].salary = _salary;
        employees[_employeeAddress].isActive = _isActive;
    }
}
